    if birth_date:
        year = birth_date.year
        month = birth_date.month
        day = birth_date.day

        if month == 2 and day == 29 and not calendar.isleap(year):
            st.error("February 29 is not valid for non-leap years. Please select a valid date.")
        else:
            # Calculate Julian Day at 0h UT
            y = year
            m = month
            d = day
            if m <= 2:
                y -= 1
                m += 12
            a = y // 100
            b = a // 4
            c = 2 - a + b
            e = int(365.25 * (y + 4716))
            f = int(30.6001 * (m + 1))
            jd = c + d + e + f - 1524.5

            # Add 0.5 for noon UT
            jd += 0.5

            # Calculate n
            n = jd - 2451545.0

            # Mean anomaly g in degrees
            g = (357.529 + 0.98560028 * n) % 360
            g_rad = math.radians(g)

            # Mean longitude L in degrees
            l = (280.459 + 0.98564736 * n) % 360

            # Apparent ecliptic longitude lambda in degrees
            lambda_deg = (l + 1.915 * math.sin(g_rad) + 0.020 * math.sin(2 * g_rad)) % 360

